from abc import ABC, abstractmethod
import requests

class Request(ABC):
    @abstractmethod
    def get(self,url):
        pass
    def get_content(self,url):
        pass

class RequestHTTP(Request):
    def get(self,url):
        try:
            resp=requests.get(url)
            return resp
        except IOError:
            print("failed to get page from url ", url)
    def get_content(self,url):
        try:
            resp=requests.get(url).content
            return resp
        except IOError:
            print("failed to get content from url ", url)

